Ext.define('PFC.view.loginForm', {
    extend: 'Ext.form.Panel',
    alias: 'widget.loginform',

    config: {
        padding: 15,
        ui: '',
        layout: {
            pack: 'center',
            type: 'vbox'
        },
        items: [
            {
                xtype: 'titlebar',
                docked: 'top',
                title: 'Processos de treball'
            },
            {
                xtype: 'label',
                html: '<center><h2>Gesti√≥ dels processos de treball</h2></center><br/>',
                itemId: 'etiqueta0'
            },
            {
                xtype: 'label',
                itemId: 'etiqueta1'
            },
            {
                xtype: 'fieldset',
                title: 'Dades de registre:',
                items: [
                    {
                        xtype: 'textfield',
                        label: 'Usuari:',
                        name: 'username',
                        required: true,
                        placeHolder: 'Nom d\'usuari'
                    },
                    {
                        xtype: 'passwordfield',
                        label: 'Clau:',
                        name: 'password',
                        required: true,
                        placeHolder: 'Clau d\'usuari'
                    }
                ]
            },
            {
                xtype: 'label',
                html: '<br/><hr/><center>Benvingut</center><br/>',
                itemId: 'etiqueta2'
            },
            {
                xtype: 'button',
                itemId: 'login',
                ui: 'forward',
                iconCls: 'action',
                iconMask: true,
                text: 'Accedir'
            }
        ],
        listeners: [
            {
                fn: 'onLoginTap',
                event: 'tap',
                delegate: '#login'
            }
        ]
    },

    onLoginTap: function(button, e, options) {
        button.up('formpanel').down('#etiqueta1').setHtml("");
        var top = button.up('viewport');

        //desem l'usuari que ha accedit
        PFC.username = button.up('formpanel').getValues().username;

        button.up('formpanel').reset();

        if (PFC.username!==""){
            button.up('formpanel').down('#etiqueta1').setHtml("");
            var x = Ext.getStore('usuariJson').findRecord("username",PFC.username,0,false,false,true);
            if (x!==null){
                top.down('#loggedInUserName').setHtml(PFC.username);
                //Desem el tipus d'usuari que estem treballant
                PFC.tipus= x.get('tipus');

                top.setActiveItem(1);

                if (PFC.tipus===0) {
                    top.down('#barraEditor').setHidden(true);
                }else{
                    top.down('#barraEditor').setHidden(false);
                }
            }else{
                button.up('formpanel').down('#etiqueta1').setHtml("<DIV style='color:red'>Necessita omplenar les dades de usuari.<br/>Els usuaris disponibles son ivan, usuari, admin i edit.</div>");
            }
        }else{
            button.up('formpanel').down('#etiqueta1').setHtml("<DIV style='color:red'>Necessita omplenar les dades de usuari.<br/>Els usuaris disponibles son ivan, usuari, admin i edit.</div>");
        }

    }

});